import styled from 'styled-components'
import { Link as LinkS } from 'react-scroll'

export const MainContainer = styled.div`
    background: #070E08;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    padding: 0 30px;
    /* height: 1000px; wersja dla wielu sekcji*/
    height: 850px;
    position: relative;
    z-index: 1;
`;

export const Background = styled.div`
    height:100%;
    width:100%;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    overflow: hidden;
`;

export const VideoBackground = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit: cover;
`;

export const MainContent = styled.div`
    z-index: 3;
    max-width: 1200px;
    top: 550px;
    margin-top: 100px;
    position: absolute;
    padding: 8px 24px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align:center;
        -ms-flex-align:center;
            align-items:center;
`;

export const ImageUploadContent = styled.div`
    z-index: 2;
    max-width: 1200px;
    height: 360px;
    position: absolute;
    padding: 8px 24px;
    display: flex;
    flex-direction: column;
    align-items:center;
`;

export const InputBar = styled.input`
    background-color: white;
    background: white;
    z-index: -1;
    position:relative;
    resize: horizontal;
    top: -35%;
    width: 330px;
    height: 42px;
    border-radius: 15px;
    border-color: white;
    text-align: center;
    font-size:1.3rem;
    text-decoration: none;

    /* &:active{
        width:auto;
        border-radius: 15px;
    }
    &:focus{
        min-width: 330px;
        border-radius: 15px;
    } */
`;

export const InputWrapper = styled.div`
    z-index: 3;
    top: 50px;
    margin-bottom: 100px;
    position: absolute;
    padding: 8px 24px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align:center;
        -ms-flex-align:center;
            align-items:center;
`;

export const SuggestionList = styled.ul`
    z-index: 3;
    border-color: black;
    width: 330px;
    top:50px;
    bottom: 0;
    max-height: 260px;
    margin-top: 10px;
    position: absolute;
    align-items: center;
    text-align: center;
    justify-content: center;
    height: 260px;
    overflow:hidden; 
    overflow-y:auto;
`;

export const MainButtonWrapper = styled.div`
    margin-top: -30px;
    /* margin-left: -60%; */
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
`;

export const Button = styled(LinkS)`
    border-radius: 50px;
    white-space: nowrap;
    /* background: ${({primary}) => (primary ? '#010606' : '#01bf71')};
    padding: ${({big}) => (big ? '14 px 48px' : '12px 30px')};
    color: ${({dark}) => (dark ? '#01bf71' : '#fff')};
    font-size: ${({fontBig}) => (fontBig ? '20px' : '16px')}; */
    background: #01bf71;
    padding: 12px 30px;
    color: #fff;
    font-size: 20px;
    outline: none;
    border: none;
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    transition: all 0.2s ease-in-out;

    &:hover{
        transition: all 0.2s ease-in-out;
        background: ${({primary}) => (primary ? '#fff' : '#fff')}; 
        color: #010606;
    }
`;

export const ImageUploadWrapper = styled.div`


`;

export const ImageButtonWrapper = styled.div`
    z-index: 2;
    margin-top: 32px;
    display: flex;
    flex-direction: column;
    align-items: center;    
`;

export const ImagePreview = styled.div`
    width:16rem;
    height:16rem;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    margin-bottom: 1rem;
    clip-path: circle(50% at 50% 50%);
    position: absolute;
    top: 65%;
    left: 50%;
    -ms-transform: translate(-50%,-50%);
    transform: translate(-50%,-50%);

`;

export const FooterWrapper = styled.footer`
    color: #fff;
    text-align: center;
    text-decoration: none;
    background-color: #0c0c0c;
    bottom: 0%;
    width: 100%;
    font-size: 0.7rem;
    padding: 10px 10px 10px 10px;
    position: fixed;
    z-index: 3;

`;
